function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
}

	//Type appropriate comment here, and begin script below
	alert('Populate Location');
	var requestedFor = new GlideAjax('x_1420970_loaner_0.LoanerAjax');
requestedFor.addParam('sysparm_name', 'locationDetail');
requestedFor.addParam('sysparm_user_name', g_form.getValue("requested_for"));
requestedFor.getXML(setlocation); /* Call HelloWorld.helloworld() with parameter sysparm_user_name set to 'Bob'
	and use the callback function HelloWorldParse() to return the result when ready */

// the callback function for returning the result from the server-side code
